        private void eΓραμματείαToolStripMenuItem_Click(object sender, EventArgs e)
        {
            label1.Text = "Γενικές Πληροφορίες για τις Ηλεκτρονικές Υπηρεσίες";
            textBox1.Text = "ΠΡΟΣΩΠΙΚΟΣ ΚΩΔΙΚΟΣ\r\n\r\nΜε την εγγραφή του φοιτητή στη γραμματεία του τμήματός του δημιουργείται προσωπικός κωδικός χρήστη (username), τον οποίο μπορεί ο φοιτητής να ενεργοποιήσει μέσω της υπηρεσίας uregister.unipi.gr, χρησιμοποιώντας το νούμερο του κινητού του τηλεφώνου ή το προσωπικό του email. Η διαχείριση και ανάκτηση του προσωπικού κωδικού (password) γίνεται στη συνέχεια μέσω της υπηρεσίας mypassword.unipi.gr.\r\n\r\nΑναλυτικές οδηγίες για τη χρήση του συστήματος uregister.unipi.gr\r\n\r\nΑναλυτικές οδηγίες για τη χρήση του συστήματος mypassword.unipi.gr\r\n\r\nΗΛΕΚΤΡΟΝΙΚΗ ΓΡΑΜΜΑΤΕΙΑ - ΣΥΣΤΗΜΑ ΠΛΗΡΟΦΟΡΗΣΗΣ ΦΟΙΤΗΤΩΝ ΓΙΑ ΣΠΟΥΔΑΣΤΙΚΑ ΘΕΜΑΤΑ\r\n\r\n" +
                            "Στη διεύθυνση sis-portal.unipi.gr λειτουργεί η διαδικτυακή εφαρμογή της Ηλεκτρονικής Γραμματείας. Από την εφαρμογή αυτή έχετε τη δυνατότητα:\r\n\r\n-να ενημερώνεστε για τα μαθήματα του προγράμματος σπουδών, τους διδάσκοντες, τα προτεινόμενα συγγράμματα κλπ.\r\n-να υποβάλλετε δηλώσεις εγγραφής και δηλώσεις μαθημάτων κάθε εξαμήνου\r\n-να ενημερώνεστε για τη βαθμολογία στα μαθήματα που έχετε εξεταστεί\r\n-να λαμβάνετε άμεσα και σε ηλεκτρονική μορφή βεβαιώσεις φοίτησης\r\nH πρόσβαση στην εφαρμογή αυτή γίνεται μέσω των προσωπικών κωδικών.\r\n\r\nΑΚΑΔΗΜΑΪΚΗ ΤΑΥΤΟΤΗΤΑ, ΦΟΙΤΗΤΙΚΟ ΕΙΣΗΤΗΡΙΟ\r\n\r\nΗ ακαδημαϊκή ταυτότητα είναι υποχρεωτική για όλους τους εγγεγραμμένους" +
                            "φοιτητές. Εκδίδεται μετά από είσοδο στη διεύθυνση academicid.minedu.gov.gr με την χρήση των προσωπικών σας κωδικών και με σχετική αίτηση που θα υποβάλλετε ηλεκτρονικά. Η ακαδημαϊκή ταυτότητα λειτουργεί και ως φοιτητικό εισιτήριο (πάσο) που διευκολύνει τις μετακινήσεις με τα μέσα μαζικής μεταφοράς.\r\n\r\nΔικαιούχοι της ακαδημαϊκής ταυτότητας είναι οι επιτυχόντες στα ΑΕΙ και ΤΕΙ της χώρας μετά την εγγραφή τους στο πρώτο έτος σπουδών. Απαραίτητη προϋπόθεση είναι η εγγραφή των πρωτοετών φοιτητών στη γραμματεία της σχολής που έχουν επιτύχει. Η νέα ταυτότητα έχει σχήμα και μορφή πιστωτικής κάρτας και είναι βελτιωμένη σε θέματα ασφάλειας έναντι της πλαστογραφίας." +
                            "\r\n\r\nΓια τους Όρους και προϋποθέσεις Συμμετοχής στην Ηλεκτρονική Υπηρεσία Απόκτησης Ακαδημαϊκής Ταυτότητας Παρακαλείσθε όπως απευθυνθείτε στη σελίδα Όροι και Προϋποθέσεις.\r\n\r\n ΠΑΝΕΠΙΣΤΗΜΙΑΚΑ ΣΥΓΓΡΑΜΜΑΤΑ \r\n\r\n Τα συγγράμματα διανέμονται μέσω της υπηρεσίας ΕΥΔΟΞΟΣ του Υπ. Παιδείας που λειτουργεί στη διεύθυνση eudoxus.gr. Στην υπηρεσία αυτή καταχωρούνται οι δηλώσεις των συγγραμμάτων κάθε εξαμήνου και παρέχεται η σχετική πληροφόρηση.\r\n\r\nΗ είσοδος στο σύστημα ΕΥΔΟΞΟΣ πραγματοποείται με τους προσωπικούς σας κωδικούς.\r\n\r\n ΑΣΥΡΜΑΤΟ ΔΙΚΤΥΟ – Wifi \r\n\r\n Όλοι οι χώροι διδασκαλίας του Πανεπιστημίου καλύπτονται από ελεύθερο ασύρματο δίκτυο wi-fi που" +
                            "ονομάζεται unipi. Σε αυτό μπορείτε να συνδεθείτε είτε με φορητό υπολογιστή είτε από το κινητό σας, χωρίς τη χρήση κάποιου κωδικού.\r\n\r\n ΥΠΗΡΕΣΙΑ ΑΠΟΜΑΚΡΥΣΜΕΝΗΣ ΠΡΟΣΒΑΣΗΣ – VPN \r\n\r\n Η υπηρεσία πρόσβασης στο εσωτερικό δίκτυο του Πανεπιστημίου (υπηρεσία VPN) παρέχει δυνατότητα χρήσης των ηλεκτρονικών υπηρεσιών του Πανεπιστημίου από απομακρυσμένες θέσεις ή δίκτυα, όπως π.χ. από οικιακές συνδέσεις Internet. Μέσω της υπηρεσίας αυτής είναι, για παράδειγμα, εφικτή η πρόσβαση στο περιεχόμενο ηλεκτρονικών επιστημονικών βιβλίων, περιοδικών και βάσεων δεδομένων που διαθέτει η βιβλιοθήκη, από θέσεις εργασίας εκτός Πανεπιστημίου. Περισσότερα στοιχεία για την υπηρεσία" +
                            "αυτή δίνονται στην ηλεκτρονική διεύθυνση www.unipi.gr/unipi/el/hu-sundesh-vpn.html.\r\n\r\n ΔΙΑΘΕΣΗ ΛΟΓΙΣΜΙΚΟΥ ΓΙΑ ΕΚΠΑΙΔΕΥΤΙΚΗ ΧΡΗΣΗ \r\n\r\n Μεταβείτε στο σύνδεσμο https://helpdesk.unipi.gr/software/ ώστε να ενημερωθείτε σχετικά με το λογισμικό που διατίθεται για εκπαιδευτική χρήση. Παρακαλείστε να ακολουθείτε πιστά τις οδηγίες εγκατάστασης.\r\n\r\n ΗΛΕΚΤΡΟΝΙΚΗ, ΕΞ ΑΠΟΣΤΑΣΕΩΣ ΕΚΠΑΙΔΕΥΣΗ (E-LEARNING) \r\n\r\n Στο Πανεπιστήμιο λειτουργούν, υπό την εποπτεία και με την επιμέλεια των Ακαδημαϊκών Τμημάτων συστήματα εξ αποστάσεως ηλεκτρονικής μάθησης, τα οποία υποστηρίζουν το εκπαιδευτικό έργο και φιλοξενούνται από το Τμήμα Μηχανοργάνωσης στη διεύθυνση" +
                            "https://eclass.unipi.gr/. Σε αυτά αναρτώνται σημειώσεις, ανακοινώσεις και λοιπό εκπαιδευτικό υλικό. Ενημέρωση για τη χρήση τους παρέχεται από τους διδάσκοντες. Πλέον του κεντρικού συστήματος eclass, ορισμένα Ακαδημαϊκά Τμήματα όπως το Τμήμα Πληροφορικής και το Τμήμα Ψηφιακών Συστημάτων διαθέτουν επίσης τμηματικά συστήματα ηλεκτρονικής μάθησης τα οποία είναι προσβάσιμα στις διευθύνσεις https://gunet2.cs.unipi.gr/ και https://evdoxos.ds.unipi.gr/ αντίστοιχα. ";
        }

        private void ηλΕξΑποστάσεωςΕκπαίδευσηToolStripMenuItem_Click(object sender, EventArgs e)
        {
            label1.Text = "URegister";
            textBox1.Text = "Η ενεργοποίηση του Ιδρυματικού Λογαριασμού πραγματοποιείται μέσω της υπηρεσίας uregister.unipi.gr. Πριν την έναρξη της διαδικασίας, ο χρήστης θα πρέπει να έχει καταχωρημένα στη Γραμματεία του τμήματός του (για φοιτητές, πανεπιστημιακούς υποτρόφους, μεταδιδάκτορες, εξωτερικούς συνεργάτες) ή τη Διεύθυνση Διοικητικού (για τακτικούς διδάσκοντες, ΠΔ407 και διοικητικό προσωπικό) το νούμερο του κινητού του τηλεφώνου ή τη διεύθυνση του email του, τον ΑΜΚΑ, τον ΑΦΜ και την ημερομηνία γέννησής του. Σε περίπτωση διόρθωσης των στοιχείων αυτών, η διαδικασία της ενεργοποίησης του Ιδρυματικού Λογαριασμού θα πρέπει να ξεκινήσει μετά το πέρας 1 τουλάχιστον ώρας, ώστε να" +
                            " έχει πραγματοποιηθεί ο ενδιάμεσος συγχρονισμός των συστημάτων.\r\n\r\nΚατά την ενεργοποίηση του λογαριασμού, τα στοιχεία του χρήστη που έχουν δηλωθεί στη Γραμματεία ή στη Διεύθυνση Προσωπικού αντιπαραβάλλονται με το Εθνικό Μητρώο ΑΜΚΑ. Σε περίπτωση που παρουσιαστεί κάποια ασυμφωνία, θα πρέπει να γίνει διόρθωση των εσφαλμένων στοιχείων στην αντίστοιχη υπηρεσία (Γραμματεία ή Διεύθυνση Προσωπικού), είτε στο Εθνικό Μητρώο ΑΜΚΑ μέσω των ΚΕΠ, εάν είναι εσφαλμένα τα στοιχεία που τηρούνται εκεί. Επιπλέον, ο χρήστης καλείται να συμφωνήσει να αποθηκεύσει το σύστημα το email του ή/και το κινητό του τηλέφωνο στην υπηρεσία mypassword.unipi.gr ώστε να μπορεί να αλλάξει" +
                            "τον κωδικό του σε περίπτωση που τον ξεχάσει. Εάν ο χρήστης δεν επιλέξει μία τουλάχιστον από τις δύο επιλογές, δεν θα μπορεί να κάνει ανάκτηση του κωδικού του και θα απαιτείται η συνδρομή των υπηρεσιών του Πανεπιστημίου για το λόγο αυτό.\r\n\r\nΌσες υπηρεσίες χρησιμοποιούν Single Sign On (πιστοποίηση μέσω ιδρυματικού λογαριασμού) για τη σύνδεση του χρήστη, αυτή γίνεται με το όνομα χρήστη (username) και τον κωδικό (password) που θα δημιουργήσει ο χρήστης κατά την εγγραφή του στο uregister.unipi.gr. Επισημαίνεται ότι μετά από επανειλημμένες αποτυχημένες προσπάθειες εισόδου σε μικρό χρονικό διάστημα, το σύστημα κλειδώνει για διάστημα 10 λεπτών.\r\n\r\n" +
                            "Παρακαλούμε ανατρέξτε για περισσότερη βοήθεια στις αναλυτικές οδηγίες για τη χρήση της υπηρεσίας uregister.unipi.gr.\r\n\r\nΓια υποστήριξη σχετικά με τον Ιδρυματικό Λογαριασμό παρακαλούνται οι χρήστες να αποστέλλουν mail στη διεύθυνση helpdesk@unipi.gr.";
        }

        private void myPasswordToolStripMenuItem_Click(object sender, EventArgs e)
        {
            label1.Text = "MyPassword";
            textBox1.Text = "Η αλλαγή του μυστικού κωδικού (password) του Ιδρυματικού Λογαριασμού πραγματοποιείται μέσω της υπηρεσίας mypassword.unipi.gr, χρησιμοποιώντας το όνομα χρήστη (username) και τον κωδικό (password) που επέλεξε ο χρήστης κατά την εγγραφή του στην υπηρεσία uregister.unipi.gr. Σε περίπτωση που ο χρήστης ξεχάσει τον κωδικό του μπορεί να τον αλλάξει χρησιμοποιώντας το όνομα χρήστη (username) και είτε το νούμερο του κινητού τηλεφώνου, είτε το εξωτερικό του email, αναλόγως τα στοιχεία που είχε επιλέξει να αποθηκευτούν κατά την εγγραφή του στο uregister.unipi.gr. Σε περίπτωση που ο χρήστης δεν είχε επιλέξει να αποθηκευτεί κάποιο από τα στοιχεία επικοινωνίας του," +
                            "δεν υπάρχει τρόπος να αλλάξει τον κωδικό του εάν τον ξεχάσει και θα πρέπει να αποστείλει σε αυτήν την περίπτωση σχετικό mail στη διεύθυνση helpdesk@unipi.gr .\r\n\r\nΜέσω της υπηρεσίας mypassword.unipi.gr μπορεί επίσης ο χρήστης να επικαιροποιήσει τα στοιχεία επικοινωνίας του όπου λαμβάνει τις ειδοποιήσεις για την αλλαγή του κωδικού του. Σημειώνεται όμως ότι κάτι τέτοιο δεν συνεπάγεται και αλλαγή των αντίστοιχων στοιχείων στη Γραμματεία ή τη Διεύθυνση Διοικητικού. Σε περίπτωση που ο χρήστης θέλει να δηλώσει αλλαγή νούμερου κινητού τηλεφώνου ή διεύθυνσης email στα στοιχεία μητρώου του, θα πρέπει να απευθυνθεί στη Γραμματεία ή τη Διεύθυνση Διοιητικού, σύμφωνα με την κατηγορία" +
                            "στην οποία ανήκει.\r\n\r\nΠαρακαλούμε ανατρέξτε για περισσότερη βοήθεια στις αναλυτικές οδηγίες για τη χρήση της υπηρεσίας mypassword.unipi.gr.\r\n\r\nΓια υποστήριξη σχετικά με τον Ιδρυματικό Λογαριασμό παρακαλούνται οι χρήστες να αποστέλλουν mail στη διεύθυνση helpdesk@unipi.gr.";
        }

        private void eΓραμματείαToolStripMenuItem1_Click(object sender, EventArgs e)
        {
            label1.Text = "Ηλεκτρονική Γραμματεία";
            textBox1.Text = "Στη διεύθυνση sis-portal.unipi.gr λειτουργεί διαδικτυακή εφαρμογή που καλύπτει τα σπουδαστικά θέματα. Από την εφαρμογή αυτή οι φοιτητές έχουν τη δυνατότητα: \r\n\r\n-να ενημερώνονται για τα μαθήματα του προγράμματος σπουδών, τους διδάσκοντες, τα προτεινόμενα συγγράμματα καθώς και τις ανακοινώσεις που εκδίδει η Γραμματεία και οι διδάσκοντες.\r\n-να ενημερώνονται για τη βαθμολογία στα μαθήματα που έχουν εξεταστεί\r\n-να υποβάλουν ηλεκτρονικά τις δηλώσεις μαθημάτων κάθε εξαμήνου\r\n-να λαμβάνουν άμεσα και σε ηλεκτρονική μορφή βεβαιώσεις φοίτησης\r\n-να υποβάλουν αιτήσεις για χορήγηση πιστοποιητικών\r\nH πρόσβαση στην εφαρμογή αυτή γίνεται μέσω του προσωπικού λογαριασμού κάθε φοιτητή.";
        }

        private void ηλToolStripMenuItem_Click(object sender, EventArgs e)
        {
            label1.Text = "Ηλεκτρονική εξ Αποστάσεως Εκπαίδευση";
            textBox1.Text = "Στο πλαίσιο της ηλεκτρονικής εξ αποστάσεως εκπαίδευσης και την εφαρμογή της όπως προβλέπεται στην εγκύκλιο του Υφυπουργού Παιδείας, Αρμόδιου για θέματα Ανώτατης Εκπαίδευσης του ΥΠΑΙΘ, το Πανεπιστήμιο Πειραιώς συνεχίζει να υποστηρίζει την ασύγχρονη διδασκαλία, ενώ από τη Δευτέρα 30/3 θα υποστηρίζεται και η σύγχρονη τηλεκπαίδευση. \r\n\r\nΟι φοιτητές/-τριες παρακαλούνται να ενημερώνονται από τις ιστοσελίδες του Ακαδημαϊκού τους Τμήματος για τις ανακοινώσεις σχετικά με τα ψηφιακά μαθήματα.\r\n\r\nΣτο Πανεπιστήμιο Πειραιά θα χρησιμοποιηθεί, όπως αναφέρθηκε, συνδυασμός των δύο παρακάτω τρόπων διεξαγωγής της εξ αποστάσεως διδασκαλίας:\r\n\r\n-Ασύγχρονη τηλεκπαίδευση" +
                            "(με ανάρτηση υλικού σε κατάλληλη πλατφόρμα)\r\n-Σύγχρονη τηλεκπαίδευση (όπου ο εκπαιδευόμενος παρακολουθεί σε πραγματικό χρόνο το μάθημα του διδάσκοντα και μπορεί να θέτει ερωτήσεις)\r\n\r\nΤο υλικό σε μορφή παρουσιάσεων ή κειμένου, εργασίες, καθώς και σύνδεσμοι (links) (Προσοχή! Όχι το υλικό καθεαυτό) σε υλικό μεγάλου όγκου (βίντεο, μαγνητοσκοπημένες διαλέξεις κλπ) θα αναρτάται στην πλατφόρμα ασύγχρονης τηλεκπαίδευσης που χρησιμοποιεί το κάθε ακαδημαϊκό τμήμα, ενώ η σύγχρονη εξ αποστάσεως διδασκαλία θα γίνεται μέσω πλατφόρμας τηλεδιασκέψεων.";
        }

        private void διδασκαλίαToolStripMenuItem_Click(object sender, EventArgs e)
        {
            label1.Text = "Διδασκαλία";
            textBox1.Text = "Ασύγχρονη διδασκαλία\r\nΒασική υποδομή για την εξ αποστάσεως επικοινωνία με τους φοιτητές και την οργάνωση της εκπαιδευτικής διαδικασίας (εκπαιδευτικό υλικό, εργασίες, ασκήσεις, κλπ) αποτελεί η πλατφόρμα Ασύγχρονης Τηλεκπαίδευσης eClass του κάθε Ακαδημαϊκού Τμήματος.\r\n\r\nΟι πλατφόρμες ασύγχρονης διδασκαλίας στο Πανεπιστήμιο είναι οι ακόλουθες ανά τμήμα:\r\n\r\n-eclass.unipi.gr (για όλα τα ακαδημαϊκά τμήματα πλην Ψηφιακών Συστημάτων και Πληροφορικής)\r\n-gunet2.cs.unipi.gr (Τμήμα Πληροφορικής)\r\n-evdoxos.ds.unipi.gr (Τμήμα Ψηφιακών Συστημάτων)\r\n\r\nΌλα τα μαθήματα θα πρέπει να διαθέτουν υλικό στο eClass και μέσα από την πλατφόρμα (στην οποία θα πρέπει" +
                            "να εγγραφούν όλοι οι φοιτητές) θα ανακοινώνεται στους φοιτητές ο τρόπος και η ώρα της σύγχρονης εξ αποστάσεως διδασκαλίας.\r\n\r\nΟ διδάσκων κάθε μαθήματος θα μπορεί να επιλέγει το καταλληλότερο για τους φοιτητές του, το μάθημά του αλλά και τον ίδιο,  τρόπο σύγχρονης εξ αποστάσεως διδασκαλίας.\r\n\r\nΟ συντονισμός της όλης διαδικασίας θα γίνεται από τον πρόεδρο του Τμήματος σε συνεργασία με τη Γραμματεία.\r\n\r\n \r\n\r\nΣύγχρονη διδασκαλία\r\nΓια τη διεξαγωγή της σύγχρονης διδασκαλίας, το Πανεπιστήμιο έχει εγκαταστήσει την πλατφόρμα Microsoft Teams, η οποία και θα είναι η κεντρικά υποστηριζόμενη λύση.\r\n\r\nΗ πλατφόρμα μπορεί να υποστηρίξει σύγχρονη" +
                            "διδασκαλία σε κοινό έως και 250 ατόμων σε πραγματικό χρόνο, μέσω της λειτουργίας Microsoft Teams Meetings. Επίσης, το Πανεπιστήμιο διαθέτει περιορισμένο αριθμό κοινόχρηστων αδειών για το υποσύστημα Microsoft Live Events, μέσω του οποίου υποστηρίζεται μετάδοση μαθήματος σε μεγάλο κοινό σε σχεδόν πραγματικό χρόνο. Η συμμετοχή των φοιτητών γίνεται μέσω μηνυμάτων κειμένου, με μικρή υστέρηση (της τάξης των 30 δευτερολέπτων).\r\n\r\nΌσοι διδάσκοντες και φοιτητές/-τριες δεν έχουν ακόμη ενεργοποιήσει το ΔΗΛΟΣ365 (https://delos365.grnet.gr) με τον ιδρυματικό τους λογαριασμό παρακαλούνται να το πράξουν για την έγκαιρη προετοιμασία και συμμετοχή τους στην εξ αποστάσεως διδασκαλία.";
        }

        private void helpDeskToolStripMenuItem_Click(object sender, EventArgs e)
        {
            label1.Text = "Υπηρεσία Υποστήριξης Χρηστών - Helpdesk";
            textBox1.Text = "Στην αίθουσα 003 του Ισογείου του κεντρικού κτιρίου, βρίσκεται αριθμός υπολογιστών από τους οποίους υπάρχει ελεύθερη πρόσβαση στο διαδίκτυο και δυνατότητα εκτύπωσης. Στον ίδιο χώρο στεγάζεται η Υπηρεσία Τεχνικής Υποστήριξης (helpdesk), το προσωπικό της οποίας καλύπτει τεχνικά τα διατμηματικά εργαστήρια, τις διοικητικές υπηρεσίες και το ακαδημαϊκό προσωπικό.";
        }

